import React, {useState, useEffect} from 'react'
import {useHistory} from 'react-router-dom'
import TopNav from '@components/top-nav';
import './styles.css';

function Login (props) {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const history = useHistory();
    // useEffect(() => {
    //     if(localStorage.getItem('user-info')){
    //         history.push("/");
    //     }
    // }, [])

    // async function login() {
    //     let item = {email, password};
    //     let result = await fetch("https://tuongtacmaytinh-server.herokuapp.com/v1/auth/login", {
    //         method: 'POST',
    //         headers: {
    //             "Content-Type": "application/json",
    //             "Accept": 'application/json',
    //         body: JSON.stringify(item)
    //         }
    //     });
    //     result = await result.json();

    //     localStorage.setItem("user-info",JSON.stringify(result));
        // history.push("/")
    // }
    const login = () => {
      history.push('/');
    }

    return (
      <main className="register-page">
      <TopNav/>
      <section className="section-register">
        <h2 className="mt-5">Login Page</h2>
        <form className="mt-4">
          <div className="form-group row register-form">
            <label  className="col-sm-3 col-form-label col-form-label-sm">Email:</label>
            <div className="col-sm-9 input-form">
              <input

                  type="email"
                  className="form-control form-control-sm"
                  placeholder="Email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
              />
            </div>
          </div>
          <div className="form-group row register-form">
            <label className="col-sm-3 col-form-label col-form-label-sm">Password:</label>
            <div className="col-sm-9 input-form">
              <input
                  type="password"
                  className="form-control form-control-sm"
                  placeholder="Password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
              />
            </div>
          </div>
          <button className="btn btn-register" type="button" onClick={login}>Login</button>
        </form>
      </section>
      </main>
    )

}

export default Login;
